@import "mixin";

$app-padding: 20px;
$container-max-widht: 600px;
body {
  background-color: #e8e8e8;
  margin: 0;
  padding: 0;
  font-family: 'Indie Flower', cursive;
  color: #212121;
}

.app-bar{
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  background-color: #03a9f4;
  color: #ffffff;
  @include box-shadow(3);

  &__container{
    display: flex;
    align-items: center;
    margin: 0 auto;
    padding: 12px $app-padding;
    max-width: $container-max-widht;
  }

  &__brand{
    flex: 1;
  }

  &__actions{
    background-color: transparent;
    border: none;
    color: #ffffff;
    border-radius: 2px;
    cursor: pointer;

    &:focus{
      outline: none;
    }

    &:hover{
      background-color: rgba(0, 0, 0, 0.08);
    }

    &--rotation{
      @include rotation();
    }

  }
}

.container {
  margin: 0 auto;
  padding: 70px $app-padding;
  max-width: $container-max-widht;
}

.new-note {
  background-color: #fff;
  padding: 10px 12px;
  border-radius: 2px;
  @include box-shadow(1);

  &__input {
    width: 100%;
    border: none;

    &:focus {
      outline: none;
    }
  }
}

.note-list {
  margin: 40px 0;
}

.note {
  display: flex;
  align-item: center;
  margin-bottom: 20px;
  background-color: #fff;
  padding: 10px 12px;
  border-radius: 2px;
  @include box-shadow(1);

  &__text{flex: 1}

  &__input{
    flex: 1;
    border-top: 0;
    border-left: 0;
    border-right: 0;
    border-bottom: 1px solid #c7c7c7;
  }

  &__button{
    background-color: transparent;
    border: 0;
    cursor: pointer;

    &--hidden{
      visibility: hidden;
    }
  }
}
